
public class SimpleMath {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int x;
			x = 7;
			x = x + 5;
			System.out.println(x);
			
		/////////////////////////////////////
			double six = 6; //designated as a double so if will be a double "6.0"
			
			/*
			 * increment x++ or ++x -> increases by one
			 * decrement x-- or -xx -> decreases by one
			 * increment is y = y + 1 
			 * increment is y = y + 1 
			 * ++y, increments and then uses
			 * y++, uses 
			 */
	
			System.out.println(++six + ++six);

		/////////////////////////////////////
			
			System.out.println(5==5); 
			System.out.println(7%1.4);
			
		
		//////////////////////////////////////
			
			/*
			 * compound operators
			 * +=, /=, -+, %=
			 * you can assign a print but you cannot declare
			 */
			
			double num = 10;
			System.out.println(num%=4);
			
			System.out.print("sup");
			System.out.println("buddy");
			
	}

}
